sys_variable_value.value:
(function execute(inputs, outputs) {


//as Twilio needs "whatsapp:+" as prefix of number but in url-encode format


	outputs.prefix=encodeURIComponent(inputs.prefix);

  gs.info("endcoded uri"+ outputs['prefix']);

    outputs.from=inputs.from.replace(/[^A-Z0-9]/ig, '');


    outputs.to = inputs.to.replace(/[^A-Z0-9]/ig, ''); // remove all special characters 



})(inputs, outputs);

