sys_variable_value.value:
(function execute(inputs, outputs) {



  if(JSUtil.notNil(inputs.from){     // check if you dynamically pass from

     outputs.from=inputs.from.replace(/[^A-Z0-9]/ig, '');
 }

    outputs.to = inputs.to.replace(/[^A-Z0-9]/ig, ''); // remove all special characters 



})(inputs, outputs);

